A script that backs up database files by copying them to a backup directory, allowing the user to specify permissions.

import shutil
import os

def copy_file(src, dst):
    shutil.copy2(src, dst)

def set_permissions(path, mode):
    os.chmod(path, mode)

def backup_database(db_dir, backup_dir, perms):
    for fname in os.listdir(db_dir):
        if fname.endswith('.db'):
            src = os.path.join(db_dir, fname)
            dst = os.path.join(backup_dir, fname)
            copy_file(src, dst)
            set_permissions(dst, perms)

