import os
import shutil

def find_duplicates(path):
    seen = {}
    for root, dirs, files in os.walk(path):
        for f in files:
            fullpath = os.path.join(root, f)
            size = os.path.getsize(fullpath)
            if size in seen:
                seen[size].append(fullpath)
            else:
                seen[size] = [fullpath]

    for size, paths in seen.items():
        if len(paths) > 1:
            for path in paths[1:]:
                print(f"Deleting {path}")
                os.remove(path)

def copy_and_set_permissions(src, dst):
