from urllib.parse import urljoin

class LinkChecker(HTMLParser):
    def __init__(self, base_url):
        self.base_url = base_url
        self.links = []

    def handle_starttag(self, tag, attrs):
        if tag == 'a':
            for attr in attrs:
                if attr[0] == 'href':
                    url = attr[1]
                    self.links.append(urljoin(self.base_url, url))

