    path = request.form['path']

    try:
        with open(path) as f:
            code = f.read()

        with open(path, 'w') as f:
            f.write(code)

        return jsonify({
            'status': 'success',
            'message': 'Linting successful!'
        }), 200, {'Content-Type': 'application/json'}

    except FileNotFoundError:
        return jsonify({
            'status': 'error',
            'message': 'File not found'
        }), 404

if __name__ == '__main__':
    app.run()

