import tarfile
import os

def add_to_archive(path):
    with tarfile.open("archive.tar", "w") as tar:
        tar.add(path)

        for root, dirs, files in os.walk(path):
            for name in files:
                filepath = os.path.join(root, name)
                tar.add(filepath)

